class EmailType extends BaseEmailType
{
	const TYPE_HOME = 1;
	const TYPE_WORK = 2;
	
	public static function findAllAsChoice() {
		$choicesArr = Doctrine_Query::create()
			->select('o.id, o.name')
		    ->from('EmailType o')
		    ->fetchArray();
		    
		$choices = array();
		foreach ($choicesArr as $value) {
			$choices[$value['id']] =  $value['name'];
		}
		
		return $choices;
	}	

}